import { StyleSheet, Text, StatusBar, Button } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack'
import SimpsonsListScreen from './screens/SimpsonsListScreen';
import SimpsonDetailScreen from './screens/SimpsonDetailScreen';
import SimpsonsDataContextProvider from './store/context/simpsons-data';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import AddSimpson from './screens/AddSimpson';
import AddSimpsonScreen from './screens/AddSimpsonScreen';

const Stack = createNativeStackNavigator();
const BottomTabs = createBottomTabNavigator();


const AddNewSimpson = () => {
  navigation.navigate('AddSimpson');
}

export default function App() {

  return (
    <>
      <StatusBar style='light' />
      <SimpsonsDataContextProvider>
        <NavigationContainer>
          <Stack.Navigator>
            <Stack.Screen name="SimpsonsList" component={SimpsonsListScreen}
              options={
                {
                  headerRight: () => {
                    return <Button title="Add Simpson" onPress={AddSimpsonScreen} />
                  }
                }
              }
            />
            <Stack.Screen name="SimpsonDetail" component={SimpsonDetailScreen} />
            <Stack.Screen name="AddSimpson" component={AddSimpson} />
          </Stack.Navigator>
        </NavigationContainer>
      </SimpsonsDataContextProvider>
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#311b3b',
    alignItems: 'stretch',
    justifyContent: 'center',
    paddingTop: '8%',
    paddingHorizontal: 16
  },
  simpContainer: {
    flex: 1,
    flexDirection: 'row',
    borderBottomWidth: 1,
  },
  listSimp: {
    paddingVertical: 16,
    flex: 3,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'flex-start'
  },
  textSimp: {
    color: '#fff'
  },
  iconContainer: {
    flex: 1,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-evenly',
  },
});
